import React, { useState } from "react";

function ResetPassword({ onLogin }) {
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [msg, setMsg] = useState("");
  const [msgClass, setMsgClass] = useState("");

const handleReset = async (e) => {
  e.preventDefault();
  try {
    const res = await fetch("http://localhost:5000/reset-password", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, newPassword }),
    });

    // Check if response is JSON
    const data = await res.json();
    setMsg(data.message);
    setMsgClass(data.success ? "text-success" : "text-danger");

    if (data.success) setTimeout(() => onLogin(), 1500);
  } catch (err) {
    console.error("Reset password fetch error:", err);
    setMsg("Server error. Make sure backend is running on port 5000.");
    setMsgClass("text-danger");
  }
};


  return (
    <form onSubmit={handleReset}>
      <h3 className="text-center mb-3">Reset Password</h3>
      <input
        className="form-control mb-2"
        type="email"
        placeholder="Registered email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        required
      />
      <input
        className="form-control mb-3"
        type="password"
        placeholder="New password"
        value={newPassword}
        onChange={e => setNewPassword(e.target.value)}
        required
      />
      <button className="btn btn-warning w-100">Reset Password</button>
      {msg && <p className={`text-center mt-2 ${msgClass}`}>{msg}</p>}
    </form>
  );
}

export default ResetPassword;
