import React, { useState } from "react";
import {Link} from ""
function Login({ onReset, onRegister }) {
  const [form, setForm] = useState({ email: "", password: "" });
  const [msg, setMsg] = useState("");
  const [msgClass, setMsgClass] = useState("");

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    const data = await res.json();
    setMsg(data.message);
    setMsgClass(data.success ? "text-success" : "text-danger");
  };

  return (
    <form onSubmit={handleLogin}>
      <h3 className="text-center mb-3">Login</h3>
      <input type="email" name="email" className="form-control mb-2" placeholder="Email" onChange={handleChange} required />
      <input type="password" name="password" className="form-control mb-2" placeholder="Password" onChange={handleChange} required />
      <button className="btn btn-success w-100 mb-2">Login</button>
<div className="d-flex justify-content-between">
        <Link to="/register" className="text-primary">Register</Link>
        <Link to="/reset-password" className="text-warning">Forgot Password?</Link>
      </div>
            {msg && <p className={`text-center ${msgClass}`}>{msg}</p>}
    </form>
  );
}

export default Login;
