import React, { useState } from "react";

function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [resetForm, setResetForm] = useState({ email: "", newPassword: "" });
  const [msg, setMsg] = useState("");
  const [showReset, setShowReset] = useState(false);
  const [msgVariant, setMsgVariant] = useState("success"); // for color: success or danger

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const handleResetChange = (e) => setResetForm({ ...resetForm, [e.target.name]: e.target.value });

  // Login handler
  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    const data = await res.json();
    setMsg(data.message);
    setMsgVariant(data.success ? "success" : "danger");
  };

  // Reset password handler
  const handleReset = async (e) => {
  e.preventDefault();
  const res = await fetch("http://localhost:5000/forgot-password", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(resetForm),
  });
  const data = await res.json();
  setMsg(data.message);
  setMsgVariant(data.success ? "success" : "danger");

  if (data.success) {
    setResetForm({ email: "", newPassword: "" });
    setShowReset(true); // switch back to login form
  }
};


  return (
    <>
      {!showReset ? (
        <form onSubmit={handleLogin}>
          <h3 className="text-center mb-3">Login</h3>
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input
              type="email"
              name="email"
              className="form-control"
              placeholder="Enter your email"
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              className="form-control"
              placeholder="Enter your password"
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-success w-100">
            Login
          </button>
          <p
            className="text-primary text-center mt-3"
            style={{ cursor: "pointer" }}
            onClick={() => setShowReset(true)}
          >
            Forgot Password?
          </p>
          {msg && (
            <p className={`text-center mt-3 fw-bold text-${msgVariant}`}>
              {msg}
            </p>
          )}
        </form>
      ) : (
        <form onSubmit={handleReset}>
          <h3 className="text-center mb-3">Reset Password</h3>
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input
              type="text"
              name="name"
              className="form-control"
              placeholder="Enter your email"
              onChange={handleResetChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">New Password</label>
            <input
              type="password"
              name="newPassword"
              className="form-control"
              placeholder="Enter new password"
              onChange={handleResetChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-warning w-100">
            Reset Password
          </button>
          <p
            className="text-primary text-center mt-3"
            style={{ cursor: "pointer" }}
            onClick={() => setShowReset(false)}
          >
            Back to Login
          </p>
          {msg && (
            <p className={`text-center mt-3 fw-bold text-${msgVariant}`}>
              {msg}
            </p>
          )}
        </form>
      )}
    </>
  );
}

export default Login;
