import React, { useState } from "react";
import { Link } from "react-router-dom";

function Login({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: email.trim(), password }),
      });
      const data = await res.json();
      setMsg(data.message);
      if (data.success) onLogin();
    } catch (err) {
      console.error(err);
      setMsg("Server error. Make sure backend is running.");
    }
  };

  return (
    <form onSubmit={handleLogin} className="w-100 mx-auto" style={{ maxWidth: "400px" }}>
      <h3 className="text-center mb-3">Login</h3>
      <input
        type="email"
        className="form-control mb-2"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        className="form-control mb-2"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit" className="btn btn-primary w-100 mb-2">Login</button>

      {/* Links centered side by side */}
      <div className="d-flex justify-content-center gap-3 mt-2">
        <Link to="/register" className="text-primary">Register</Link>
        <Link to="/reset-password" className="text-warning">Forgot Password?</Link>
      </div>

      {msg && <p className="text-center mt-2 text-danger">{msg}</p>}
    </form>
  );
}

export default Login;
