import React, { useState } from "react";

function ResetPassword({ onLogin }) {
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const handleReset = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/reset-password", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, newPassword }),
    });
    if (res.ok) {
      alert("Password reset successful!");
      onLogin(); // go to login
    } else {
      alert("Error resetting password!");
    }
  };

  return (
    <form onSubmit={handleReset}>
      <h3 className="text-center mb-3">Reset Password</h3>
      <input className="form-control mb-2" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <input className="form-control mb-3" type="password" placeholder="New Password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} required />
      <button className="btn btn-dark w-100">Reset Password</button>
    </form>
  );
}

export default ResetPassword;
