import React, { useState } from "react";

function Login({ onForgot, onSwitch }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });
    if (res.ok) {
      alert("Login successful!");
    } else {
      alert("Invalid credentials!");
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <h3 className="text-center mb-3">Login</h3>
      <input className="form-control mb-2" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <input className="form-control mb-2" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
      <button className="btn btn-dark w-100 mb-2">Login</button>
      <div className="text-center">
        <small className="text-primary" role="button" onClick={onForgot}>Forgot Password?</small><br />
        <small>Donâ€™t have an account? <span className="text-primary" role="button" onClick={onSwitch}>Register</span></small>
      </div>
    </form>
  );
}

export default Login;
