import React, { useState } from "react";

function Register({ onSwitch }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });
    if (res.ok) {
      alert("Registered successfully!");
      onSwitch(); // Go to login page
    } else {
      alert("User already exists or error!");
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <h3 className="text-center mb-3">Register</h3>
      <input className="form-control mb-2" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <input className="form-control mb-3" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
      <button className="btn btn-dark w-100">Register</button>
    </form>
  );
}

export default Register;
