import React, { useState } from "react";

function Register({ onSwitch }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");
  const [msgClass, setMsgClass] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email, password }),
    });
    const data = await res.json();
    setMsg(data.message);
    setMsgClass(data.success ? "text-success" : "text-danger");
    if (data.success) setTimeout(() => onSwitch(), 1500); // redirect to login
  };

  return (
    <form onSubmit={handleRegister}>
      <h3 className="text-center mb-3">Register</h3>
      <input
        className="form-control mb-2"
        type="text"
        placeholder="Name"
        value={name}
        onChange={e => setName(e.target.value)}
        required
      />
      <input
        className="form-control mb-2"
        type="email"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        required
      />
      <input
        className="form-control mb-3"
        type="password"
        placeholder="Password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        required
      />
      <button className="btn btn-primary w-100">Register</button>
      <div className="text-center mt-2">
        <small>Already have an account? <span className="text-primary" role="button" onClick={onSwitch}>Login</span></small>
      </div>
      {msg && <p className={`text-center mt-2 ${msgClass}`}>{msg}</p>}
    </form>
  );
}

export default Register;
