import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Register from "./Register";
import Login from "./Login";
import ForgotPassword from "./Forgotpassword";
import ResetPassword from "./Rese";

function App() {
  const [page, setPage] = useState("login");

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-dark bg-dark">
        <div className="container-fluid d-flex justify-content-between">
          <a className="navbar-brand fw-bold text-white" href="#">
            My Company
          </a>
          <div>
            <button
              className={`btn btn-sm me-2 ${
                page === "register" ? "btn-light" : "btn-outline-light"
              }`}
              onClick={() => setPage("register")}
            >
              Register
            </button>
            <button
              className={`btn btn-sm ${
                page === "login" ? "btn-light" : "btn-outline-light"
              }`}
              onClick={() => setPage("login")}
            >
              Login
            </button>
          </div>
        </div>
      </nav>

      {/* Page content */}
      <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
        <div className="card p-4 shadow" style={{ width: "100%", maxWidth: "400px" }}>
          {page === "register" && <Register onSwitch={() => setPage("login")} />}
          {page === "login" && <Login onForgot={() => setPage("forgot")} onSwitch={() => setPage("register")} />}
          {page === "forgot" && <ForgotPassword onReset={() => setPage("reset")} />}
          {page === "reset" && <ResetPassword onLogin={() => setPage("login")} />}
        </div>
      </div>
    </>
  );
}

export default App;
